mapper.select:
SELECT TX.USER_NO, TX.GENDER, TX.AGE, TX.BIRTHDATE, TX.MBTI, TX.RESIDENCE , TX.WORK_TYPE, TX.WORK_DETAIL, TX.ELEMENTARY, TX.MIDDLE, TX.HIGH, TX.UNIV , TX.BLOOD_TYPE, TX.PROFILE_ID, TX.MOTTO, TM.USER_NAME, TM.TELNO1, TM.EMAIL FROM MEMBER TM INNER JOIN( SELECT TC.USER_NO, TC.GENDER, TC.AGE, TC.BIRTHDATE, TC.MBTI, TC.RESIDENCE , TC.WORK_TYPE, TC.WORK_DETAIL, TC.ELEMENTARY, TC.MIDDLE, TC.HIGH, TC.UNIV , TC.BLOOD_TYPE, TC.PROFILE_ID, TC.MOTTO FROM CARD TC INNER JOIN( SELECT SUB_NO FROM FRIENDS WHERE 1=1
AND USER_NO = #{userNo}
UNION SELECT USER_NO FROM FRIENDS WHERE 1=1
AND SUB_NO = #{userNo}
)TF ON TF.SUB_NO = TC.USER_NO )TX ON TX.USER_NO = TM.USER_NO WHERE 1=1

		SELECT EXISTS(
					SELECT *
        			FROM FRIENDS
        			WHERE 1=1

        				AND USER_NO = #{userNo} 


        				AND SUB_NO = #{subNo} 

        			LIMIT 1) AS RESULT;

mapper.insert:
INSERT INTO FRIENDS
	(
		USER_NO
	,	SUB_NO
	,	REG_DT
	)
VALUES
	(
		#{userNo}
	,	#{subNo}
	,	DATE_FORMAT(SYSDATE(),'%Y%m%d%H%i%s')
	)

INSERT INTO HISTORY
	(
		SEQ
	,	REG_DT
	)
VALUES
	(
		#{seq}
	,	DATE_FORMAT(SYSDATE(),'%Y%m%d%H%i%s')
	)

mapper.select:
		SELECT SEQ
		FROM FRIENDS
		WHERE 1=1

        	AND USER_NO = #{userNo} 


        	AND SUB_NO = #{subNo}
