mapper.select:
SELECT COUNT(TH.SEQ) AS TOTAL
FROM HISTORY TH
INNER JOIN(
	SELECT SEQ
	FROM FRIENDS
	WHERE USER_NO = #{userNo}
		AND SUB_NO=#{subNo}
)TF ON TF.SEQ = TH.SEQ
WHERE 1=1

SELECT LATITUDE, LONGITUDE, DATE_FORMAT(REG_DT, '%Y-%m-%d %H:%m') AS REG_DT FROM HISTORY WHERE 1=1
AND SEQ = #{seq}
ORDER BY REG_DT DESC
